mapper.select:
SELECT * FROM `electrombile`


		recorder_id = #{recorderID}


		AND elect_state = #{electState}


		AND insur_detail = #{insurDetail}


		AND pro_id = #{proID}


		AND city_id = #{cityID}


		AND area_id = #{areaID}


		AND pro_id = #{proPower}


		AND city_id = #{cityPower}


		AND area_id = #{areaPower}


		AND owner_tele = #{ownerTele}


		AND owner_id = #{ownerID}


		AND plate_num = #{plateNum}


		AND gua_card_num = #{guaCardNum}


		AND owner_name like "%"#{ownerName}"%"


		AND recorder_time >= #{startTime}


		AND recorder_time <= #{endTime}


order by `recorder_time` desc

SELECT * FROM `electrombile`


	    pro_id = #{proPower}


		AND city_id = #{cityPower}


		AND area_id = #{areaPower}


order by `recorder_time` desc

SELECT * FROM `electrombile`


	    owner_tele = #{tele}


order by `recorder_time` desc
