mapper.insert:
insert into people (people_id, people_gua_card_num, people_tele, people_name, people_sex, people_id_cards, people_age, people_type, people_status, guardian_name, guardian_tele, guardian_relation, people_pic, contact_address, recorder_id, recorder_time, pro_id, city_id, area_id
) values (#{people_id,jdbcType=INTEGER}, #{people_gua_card_num,jdbcType=INTEGER}, #{people_tele,jdbcType=VARCHAR}, #{people_name,jdbcType=VARCHAR}, #{people_sex,jdbcType=VARCHAR}, #{people_id_cards,jdbcType=DATE}, #{people_age,jdbcType=VARCHAR}, #{people_type,jdbcType=VARCHAR}, #{people_status,jdbcType=VARCHAR}, #{guardian_name,jdbcType=INTEGER}, #{guardian_tele,jdbcType=INTEGER}, #{guardian_relation,jdbcType=VARCHAR}, #{people_pic,jdbcType=VARCHAR}, #{contact_address,jdbcType=VARCHAR}, #{recorder_id,jdbcType=INTEGER}, now(), #{pro_id,jdbcType=INTEGER}, #{city_id,jdbcType=INTEGER}, #{area_id,jdbcType=INTEGER}
)

mapper.select:
SELECT * FROM `people`


		recorder_id = #{recorderID}


		AND people_type = #{peopleType}



		AND pro_id = #{proID}


		AND city_id = #{cityID}


		AND area_id = #{areaID}


		AND pro_id = #{proPower}


		AND city_id = #{cityPower}


		AND area_id = #{areaPower}


		AND people_tele like "%"#{peopleTele}"%"


		AND people_id_cards like "%"#{peopleIdCards}"%"


		AND people_gua_card_num like "%"#{peopleGuaCardNum}"%"


		AND people_name like "%"#{peopleName}"%"


		AND guardian_name like "%"#{guardianName}"%"


		AND guardian_tele like "%"#{guardianTele}"%"


		AND recorder_time >= #{startTime}


		AND recorder_time <= #{endTime}


order by `recorder_time` desc

SELECT * FROM `people`


	    pro_id = #{proPower}


		AND city_id = #{cityPower}


		AND area_id = #{areaPower}


order by `recorder_time` desc

SELECT * FROM `people`


	    people_tele = #{tele}


order by `recorder_time` desc

SELECT * FROM `people`


	    recorder_id = #{recorderId}


order by `recorder_time` desc

SELECT * FROM `people`


	    people_gua_card_num = #{guaCardNum}


	    AND people_tele = #{peopleTele}


order by `recorder_time` desc

SELECT * FROM `people`


	    pro_id = #{proPower}


		AND city_id = #{cityPower}


