mapper.select:
SELECT * FROM `station`


		station_status = #{stationStatus}


		AND station_phy_num = #{stationPhyNum}


		AND station_name like "%"#{stationName}"%"


		AND install_date >= #{startTime}


		AND install_date <= #{endTime}


order by `install_date` desc

